import styled from 'styled-components';

export const BackgroundText = styled.span`
  position: absolute;
  font-size: 10rem;
  opacity: 0.2;
  font-weight: bold;
  color: #8b8b8b;
  top: 10rem;
  left: 10rem;
`

export const VerticalText = styled.span`
  writing-mode: tb-rl;
  transform:rotate(180deg);
  font-size:20px;
  color: slateblue;
  position: absolute;
  top: 12.5rem;


`

export const VerticalLine = styled.div`
  content: " sss";
  height: 5rem;
  width: 0.1rem;
  background: slateblue;

`
