import styled from "styled-components";

export const StyledLink = styled.span`
    & a {
      font-size: 1.5rem;
      color: black;
      text-decoration: none;
      display: block;
      padding: 0.7rem;
      rgba(106, 90, 205, 0.1)
    }
`
