import styled, {css} from "styled-components";

export const Wrapper = styled.div`
  max-width: 400px;
  margin: 0 auto;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

export const Title = styled.h1(
    (isChange) => css`
    color: ${isChange ? 'red' : 'green'}  
    font-size: 20px;
`);
