import styled from "styled-components";

export const StyledLink = styled.span`
    & a {
      font-size: 1.5rem;
      text-decoration: none;
      display: block;
      padding: 0.7rem;
      background: rgba(106, 90, 205, 0.3);
      width: 4.5rem;
      border-radius: 10px;
      color: black;
      border: 2px solid slateblue;
    }
`
