import styled from "styled-components";

export const StyledInput = styled.input`
    width: 150px;
    border: 1px solid slateblue;
    margin: 20px;
    &:focus {
      box-shadow: 0 0 10px rgba(0,0,0,0.5);
    }

  ${active &&
  css`
        border-left: 2px solid ${colors.green};
        background-color: ${colors.greyF5};
        color: ${colors.green};
      `}
`
