import styled from "styled-components";

export const StyledLink = styled.span`
    & a {
      font-size: 1.5rem;
      text-decoration: none;
      display: block;
      padding: 0.7rem;
      background: slateblue;
      width: 4.5rem;
      color: white;
      border: 2px solid slateblue;
      position: absolute;
      top:23rem;
      left: 12rem;
      box-shadow: 0 0 10px rgba(0,0,0,0.5);

    }
`
