import styled from "styled-components";

export const StyledItem = styled.li`
    list-style: none;
    display: block;
    border: 2px solid slateblue;
    background-color: rgba(106, 90, 205, 0.1);
    margin: 10px;
    border-radius: 5px;
    padding: 7px 7px 0 7px;
    box-shadow: 0 0 7px rgba(0,0,0,0.3);


`
