import styled from 'styled-components';

export const BackgroundText = styled.span`
  position: absolute;
  font-size: 10rem;
  opacity: 0.2;
  font-weight: bold;
  color: #8b8b8b;
  top: 10rem;
  left: 10rem;
  text-shadow: 1px 1px 2px black;
`

export const VerticalLine = styled.div`
  content: " sss";
  height: 100vh;
  width: 0.7rem;
  background: slateblue;

`
